// Generar un número aleatorio en un rango específico
function getRandomNumber(min, max) {
    return Math.random() * (max - min) + min;
}
var lv_active_power = getRandomNumber(0, 100); // Potencia activa LV (kW)
var wind_speed = getRandomNumber(0, 25); // Velocidad del viento (m/s)
var theoretical_power_curve = getRandomNumber(0, 500); // Curva de potencia teórica (KWh)
var wind_direction = getRandomNumber(0, 360); // Dirección del viento (°)

// Construir el punto de datos
msg.payload = [
    {
        measurement: "turbina",
        fields: {
            lv_active_power: lv_active_power,
            wind_speed: wind_speed,
            theoretical_power_curve: theoretical_power_curve,
            wind_direction: wind_direction
        },
        tags: {
            turbine_id: "T2"
        },
        timestamp: new Date()
    }
]
return msg;
